<p>{navigation_pathway}</p>
<p>{navigation_childlist}</p>
<table border="0" align="center" style="width: 100%;" class="htmtableborders">
  <tbody>
	<tr>
	  <td rowspan="1">
	  <h1>{product_name} {edit_link}</h1>&nbsp;{manufacturer_link}
	  </td>
<td align="center" valign="top" rowspan="4">{product_image}<br/><br/>{more_images}</td>
	</tr>
	<tr>
	  <td rowspan="1"><font size="2">{product_price}</font><br /></td>
	</tr>
	<tr>
	  <td style="text-align: center;"><br /></td>
	</tr>
	<tr>
	  <td rowspan="1"><span style="font-style: italic;"></span><hr style="width: 100%; height: 2px;" /><span style="font-style: italic;">{product_description}</span>
	  <br/><span style="font-style: italic;">{file_list}</span></td>
	</tr>
	<tr>
	  <td><hr style="width: 100%; height: 2px;" /><div style="text-align: center;"><span style="font-style: italic;"></span>{addtocart}<span style="font-style: italic;"></span></div></td>
	  <td style="text-align: center;" rowspan="1">{product_availability}</td>
	</tr>
	<tr>
	  <td colspan="2">{product_type}</td>
	</tr>
	<tr>
	  <td colspan="2"><hr />{product_reviews}</td>
	</tr>
	<tr>
	  <td colspan="2">{product_reviewform}<br /></td>
	</tr>
	<tr>
	  <td colspan="2">{related_products}<br /></td>
	</tr>
	<tr>
	  <td rowspan="1" colspan="2"><div style="text-align: center;">{vendor_link}<br /></div><br /></td>
	</tr>
  </tbody>
</table>
